import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { User } from 'src/app/models/user';
import { UserService } from 'src/app/services/user.service';

@Component({
  selector: 'app-edit-user',
  templateUrl: './edit-user.component.html',
  styleUrls: ['./edit-user.component.css'],
})
export class EditHeroComponent implements OnInit {
  @Input() user?: User;
  @Output() useresUpdated = new EventEmitter<User[]>();

  constructor(private userService: UserService) {}

  ngOnInit(): void {}

  updateHero(user: User) {
    this.userService
      .updateUser(user)
      .subscribe((users: User[]) => this.useresUpdated.emit(users));
  }

  deleteUser(hero: User) {
    this.userService
      .deleteUser(user)
      .subscribe((heroes: User[]) => this.useresUpdated.emit(heroes));
  }

  createHero(hero: User) {
    this.userService
      .createHero(hero)
      .subscribe((heroes: User[]) => this.useresUpdated.emit(heroes));
  }
}

<form>
  <div *ngIf="hero">
    <h2>{{ hero.name }}</h2>

    <div class="form-group">
      <label for="">Name:</label>
      <input class="form-control" [(ngModel)]="hero.name" placeholder="Name" />

    </div>
    <div class="form-group">
      <label for="">firstName:</label>
      <input class="form-control" [(ngModel)]="hero.firstName" placeholder="firstName" />

    </div>
    <div class="form-group">
      <label for="">Last Name:</label>
      <input class="form-control" [(ngModel)]="hero.lastName" placeholder="Last name" />

    </div>
    <div class="form-group">
      <label for="">Email:</label>
      <input class="form-control" [(ngModel)]="hero.email" placeholder="Email" />

    </div>

    <button class="btn btn-primary" (click)="updateHero(hero)" *ngIf="hero.id">Save</button>
    <button class="btn btn-primary" (click)="deleteHero(hero)" *ngIf="hero.id">Delete</button>
    <button class="btn btn-primary" (click)="createHero(hero)" *ngIf="!hero.id">Create</button>

  </div>
</form>
